/// <summary>Renames the page. Redirection from old title to new title is /// automatically created.</summary> /// <param name="newTitle">New title of this page.</param> /// <param name="reason">Reason for renaming.</param> public void RenameTo(string newTitle, string reason) { RenameTo(newTitle, reason, false, false); }/// <summary>Renames the page. Redirection from old title to new title is /// automatically created.</summary> /// <param name="newTitle">New title of this page.</param> /// <param name="reason">Reason for renaming.</param> /// <param name="renameTalkPage">If true, corresponding talk page will /// also be renamed.</param> /// <param name="renameSubPages">If true, subpages (like User:Me/Subpage) /// will also be renamed.</param> public void RenameTo(string newTitle, string reason, bool renameTalkPage, bool renameSubPages) { if (string.IsNullOrEmpty(newTitle)) throw new ArgumentNullException("newTitle"); if (string.IsNullOrEmpty(title)) throw new WikiBotException(Bot.Msg("No title is specified for page to rename.")); if (Bot.askConfirm) { Console.Write("

" + Bot.Msg("The page "{0}" is going to be renamed to "{1}".
"), title, newTitle); if(!Bot.UserConfirms()) return; } if (site.useApi) { string token = ""; if (site.tokens != null && site.tokens.ContainsKey("csrftoken")) token = site.tokens["csrftoken"]; else { var tokens = GetSecurityTokens("move"); if (tokens.ContainsKey("missing")) throw new WikiBotException( string.Format(Bot.Msg("Page "{0}" doesn't exist."), title)); if (!tokens.ContainsKey("movetoken") || tokens["movetoken"] == "") throw new WikiBotException(string.Format( Bot.Msg("Unable to rename page "{0}" to "{1}"."), title, newTitle)); token = tokens["movetoken"]; } string postData = string.Format("from={0}&to={1}&reason={2}{3}{4}&token={5}", Bot.UrlEncode(title), Bot.UrlEncode(newTitle), Bot.UrlEncode(reason), renameTalkPage ? "&movetalk=1" : "", renameSubPages ? "&movesubpages=1" : "", Bot.UrlEncode(token)); string respStr = site.PostDataAndGetResult(site.apiPath + "?action=move" + "&format=xml", postData); if (respStr.Contains("<error")) throw new WikiBotException(string.Format( Bot.Msg("Failed to rename page "{0}" to "{1}"."), title, newTitle)); } else { string respStr = site.GetWebPage(site.indexPath + "?title=Special:Movepage/" + Bot.UrlEncode(title)); Match m = site.regexes["editToken"].Match(respStr); string securityToken = string.IsNullOrEmpty(m.Groups[1].Value) ? m.Groups[2].Value : m.Groups[1].Value; if (string.IsNullOrEmpty(securityToken)) { Console.Error.WriteLine( Bot.Msg("Unable to rename page "{0}" to "{1}"."), title, newTitle); return; } if (site.watchList == null) { site.watchList = new PageList(site); site.watchList.FillFromWatchList(); } watched = site.watchList.Contains(this); string postData = string.Format("wpNewTitle={0}&wpOldTitle={1}&wpEditToken={2}" + "&wpReason={3}{4}{5}{6}", Bot.UrlEncode(newTitle), Bot.UrlEncode(title), Bot.UrlEncode(securityToken), Bot.UrlEncode(reason), renameTalkPage ? "&wpMovetalk=1" : "", renameSubPages ? "&wpMovesubpages=1" : "", watched ? "&wpWatch=1" : ""); respStr = site.PostDataAndGetResult(site.indexPath + "?title=Special:Movepage&action=submit", postData); if (site.messages == null) site.LoadMediawikiMessages(true); Regex successMsg = new Regex( "<h1[^>]*>(<span[^>]*>)?\s*" + site.messages["pagemovedsub"] + "\s*<"); if (!successMsg.IsMatch(respStr)) throw new WikiBotException(string.Format( Bot.Msg("Failed to rename page "{0}" to "{1}"."), title, newTitle)); } title = newTitle; Console.WriteLine( Bot.Msg("Page "{0}" has been successfully renamed to "{1}"."), title, newTitle); }
